loginUrl:
// 登录及登录检查
function login_(openBrowser, checkMode) {
    // java.removeCookie("snssdk.com")
    var cookie_ = String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap() ? source.getLoginInfoMap() : {})['手动登录Token']
    // java.toast(cookie_)
    if (cookie_ && cookie_ != "" && !checkMode) {
        if (!source.variableComment) {
            java.toast("请不要重复登录，请先退出登录！")
        } else {
            java.toast("您为填写token登录，请手动移除token后再次登录")
        }
        return false
    }
    if (openBrowser) {
        try {
            java.startBrowserAwait("https://fanqienovel.com/", "登录")
        } catch (e) {
            java.toast(e)
        }
    }
    // java.log("snssdk cookie: " + java.getCookie("snssdk.com") + "will be reomved")
    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {}
    // java.log(cookie)
    var cookie_ = "sessionid=" + String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap())['手动登录Token']
    let user
    try {
      user = JSON.parse(java.ajax("https://fanqienovel.com/api/user/info/v2," + JSON.stringify({
        method: "GET",
        headers: {
          "Cookie": cookie_
        }
      }))).data.name
     } catch (e) {java.log(e)}
    if (!cookie_ || cookie_ == "sessionid=" || !user) {
        java.toast("未获取到登录凭据，登录失败")
        return false
    }
    java.toast("欢迎 " + user + "\n登录成功！")
    return true
}

function login() // 一定程度上加上这个函数能够支持更多的版本
{}

function l2(a, b) {
  try {
    login_(a, b)
  } catch (e) {
    java.log(e+"\n"+e.stack)
  }
}

// 取消登录
function logout() {
    cookie.removeCookie("fanqienovel.com");
    cookie.removeCookie("snssdk.com");
    if (String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap() ? source.getLoginInfoMap() : {})['手动登录Token']) java.toast("请手动移除填写的Token")
    else java.toast("退出登录成功");
}

function logToken() {
  java.log(
    String(
      cookie.getKey(
        "fanqienovel.com",
        "sessionid"
      )
    ) ? String(
     cookie.getKey(
        "fanqienovel.com", 
        "sessionid"
      )
    ) : (
     source.getLoginInfoMap()
      ? source.getLoginInfoMap()
      : {}
    )['手动登录Token']
  )
  java.toast("登录Token已打印到日志，请妥善保管")
}

function tutorial() {
  java.longToast("\n手动登录token功能用于部分用户的跨端登录，不知道是什么的留空就行\n普通用户请使用下方的\"账号登录\"按钮登录自己的账号")
}

