let parts = String(result).split(',');
let isFloat=function (str) {
    return !isNaN(parseFloat(str)) && isFinite(str);
  }
let arr = [];
for (let i = 0; i < parts.length; i++) {
    if (parts[i].includes('{')) continue;
    t = /name=([^}]+)/gm.exec(parts[i]);
    if (t != null) {
        arr.push(t[1]);
        continue;
    }
    if (i < parts.length - 1) {
        if (isFloat(parts[i]) && !isFloat(parts[i + 1])) {
            arr.push(parts[i] + parts[i + 1]);
            i++;
        } else {
            arr.push(parts[i]);
        }

    } else {
        arr.push(parts[i].replace(']',''));
    }
} 
arr.toString();

