@js:var result = [];
var na = java.getStringList("#chapterList li@text");
var g = [];
var src = java.getElements("#chapterList li")
var fla = String(src).split("\n");
fla.forEach((number, index) => {
  if (number.includes("volumes")) {
    g.push(index);
  }
 });
var BKid = baseUrl.match(/\d+/);
var FCPul = java.getString("#chapterList a.0@href");
var FCPid = FCPul.match(/\/(\d+)\.html/)[1];
for (var i = 0; i < na.length; i++) {
	var volume = g.includes(i) ? 'true' : 'false';
    var CPid = parseInt(FCPid) + i;
    var CPul = g.includes(i) ? '' : '/book/' + String(BKid) + '/' + String(CPid) + '.html';
    result.push({
        ChapterName: na[i],
        chapterUrl: CPul ,
        isVolume: volume
    });
}
result;

