}
http://iyc.ifeng.com/api/books/@get:{bid}/chapters/{{$.chapterNum}}/content
$.data.txt
*/
//警告⚠️勿动
function m(bArr) {
	i = 0;
	for (i2 = 0; i2 < 2; i2++) {
		i = parseInt(i) + parseInt(bArr[i2] < 0 ? (bArr[i2] + 256) : bArr[i2] << (i2 * 8));
  }
  return i;
}
function decode(bArr){
	length = bArr.length / 2;
  for (i = 0,str = ''; i < length; i++) {
  	i2 = i * 2;
  	b = bArr[i2];
   b2 = bArr[i2 + 1];
   if (b == -1 && b2 == -2) {str += ' ';} 
   else if (b == 13 && b2 == 0) {str += ' ';} 
   else {
     str += String.fromCharCode(((b2 << 8) & 65280) | (b & 255));
   }
  }
  return str;
}

